import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Newproduct from "./Newproduct";
import styles from "./Header.module.css";
import { signOut } from "../../redux/features/application";
import basket from "../../assets/basket.png";

// import { signOut } from "../../redux/features/application";

const Title = () => {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.application.token);
  const role = useSelector((state) => state.application.role);
  const messageAdmin = useSelector((state) => state.application.message);
  const messageUser = useSelector((state) => state.application.message);
  console.log(role, token);
  const [addProduct, setAddProduct] = useState(false);
  const handleOpenLogoutForm = () => {
    dispatch(signOut());
  };
  const handleAddProduct = () => {
    addProduct ? setAddProduct(false) : setAddProduct(true);
  };
  return (
    <div className={styles.Header}>
      <div className={styles.title}>
        <h1 className={styles.myShop}>
          <Link to="/" className={styles.link}>
            My Online Shop
          </Link>
        </h1>
        <div>
          {token && role === "admin" ? (
            <div className={styles.headerBlock}>
              <div onClick={handleAddProduct} className={styles.addNewProduct}>
                добавить продукт
              </div>
              {addProduct && <Newproduct />}
              <div>
                <h4>{messageAdmin}</h4>
              </div>
              <div>
                <span
                  onClick={handleOpenLogoutForm}
                  className={styles.buttonLog}
                >
                  Выйти
                </span>
              </div>
            </div>
          ) : token && role === "user" ? (
            <div className={styles.userLK}>
              <div className={styles.basket}>
                <img src={basket} alt="" />
              </div>
              <div>
                <span>{messageUser}</span>
              </div>
              <div>
                <span
                  onClick={handleOpenLogoutForm}
                  className={styles.buttonLog}
                >
                  Выйти
                </span>
              </div>
            </div>
          ) : (
            <div>
              <Link to="/login" className={styles.buttonLog}>
                Войти
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Title;
