const inittialState = {
  basket: [
    // { id, left, amount },
    // { id, left, amount },
  ],
  finally: 0,
  error: null,
};

const basket = (state = inittialState, action) => {
  switch (action.type) {
    case "get/basket/pending":
      return {
        ...state,
        geting: true,
        error: null,
      };
    case "get/basket/fulfilled":
      console.log(action.payload.products.products);
      const productInBasketSum = action.payload.products.products.reduce(
        (finallySum, product) => {
          console.log(product._id);
          return finallySum;
        }
      );
      console.log(productInBasketSum);
      return {
        ...state,
        geting: false,
        basket: [...action.payload.basket.products.map((product) => product)],
        finally: productInBasketSum,
      };
    case "get/basket/rejected":
      return {
        ...state,
        geting: false,
        error: action.error,
      };
    case "addproduct/inbasket/pending":
      return {
        ...state,
        adding: true,
        error: null,
      };
    case "addproduct/inbasket/fulfilled":
      const id = action.payload.productinBasket._id;
      const left = action.payload.productinBasket.left--;
      console.log(action.payload.productLeft);
      return {
        ...state,
        adding: false,
        basket: [...state.basket, { id, left, amount: 1 }],
        finally: action.payload.productLeft.reduce((sum, product) => {
          if (product._id === id) {
            sum += product.price;
          }
          return sum;
        }, 0),
      };
    case "addproduct/inbasket/rejected":
      return {
        ...state,
        adding: false,
        error: action.error,
      };
    case "deleteproduct/frombasket/pending":
      return {
        ...state,
        deleting: true,
        error: null,
      };
    case "deleteproduct/inbasket/fulfilled":
      return {
        ...state,
        deleting: false,
        basket: [
          ...state.basket.filter((product) => product.id !== action.payload),
        ],
      };
    case "deleteproduct/inbasket/rejected":
      return {
        ...state,
        deleting: false,
        error: action.error,
      };
    case "addamount/productinbasket/pending":
      return {
        ...state,
        addingAmout: true,
        error: null,
      };
    case "addamount/productinbasket/fulfilled":
      return {
        ...state,
        addingAmout: false,
        basket: [
          ...state.basket.map((product) => {
            if (product.id === action.payload) {
              if (product.left > 0) {
                product.left -= 1;
                product.amount += 1;
              }
            }
            return product;
          }),
        ],
      };
    case "addamount/productinbasket/rejected":
      return {
        ...state,
        addingAmout: false,
        error: action.error,
      };
    case "removeamount/productinbasket/pending":
      return {
        ...state,
        removeAmout: true,
        error: null,
      };
    case "removeamount/productinbasket/fulfilled":
      return {
        ...state,
        removeAmout: false,
        basket: [
          ...state.basket.map((product) => {
            if (product.id === action.payload) {
              if (product.amount > 1) {
                product.left += 1;
                product.amount -= 1;
              }
            }
            return product;
          }),
        ],
      };
    case "removeamount/productinbasket/rejected":
      return {
        ...state,
        removeAmout: false,
        error: action.error,
      };
    default:
      return state;
  }
};

export const getAllProductsInBasket = () => {
  return async (dispatch, getState) => {
    const state = getState();
    dispatch({ type: "get/basket/pending" });
    try {
      const res = await fetch("http://localhost:4000/basket", {
        headers: {
