import React from "react";
import { useSelector } from "react-redux";
import { Navigate, Route, Routes } from "react-router-dom";
// import Admin from "./Header/Admin";
import Header from "./Header/Header";
import SigninForm from "./Header/SigninForms/SigninForm";
import SignupForm from "./Header/SigninForms/SignupForm";

import Main from "./Main/Main";
import Products from "./Main/Products/Products";

const App = () => {
  const token = useSelector((state) => state.application.token);
  const role = useSelector((state) => state.application.role);
  const categories = useSelector((state) => state.category.category);
  const products = useSelector((state) => state.products.products);

  if (!token) {
    return (
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={
              <div>
                <Header />
                <Main />
                <Products />
              </div>
            }
          />
          {categories.map((category) => {
            return (
              <Route
                path={`/${category.name}`}
                element={
                  <Products
                    products={products.filter(
                      (product) => product.category === category.name
                    )}
                  />
                }
              />
            );
          })}
          <Route path="/login" element={<SigninForm />} />
          <Route path="/logup" element={<SignupForm />} />
          <Route path="/admin" element={<Navigate to="/" replace />} />
          <Route path="/user" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    );
  } else if (role === "admin") {
    return (
      <div className="App">
        <Routes>
          <Route
            path="/admin"
            element={
              <div>
                <Header />
                <Main />
                <Products />
              </div>
            }
          />
          <Route
            path="/"
            element={
              <div>
                <Header />
                <Main />
                <Products />
              </div>
            }
          />
          {categories.map((category) => {
            return (
              <Route
                path={`/${category.name}`}
                element={
                  <Products
                    products={products.filter(
                      (product) => product.category === category.name
                    )}
                  />
                }
              />
            );
          })}
          <Route path="/login" element={<Navigate to="/admin" replace />} />
        </Routes>
      </div>
    );
  } else if (role === "user") {
    return (
      <div className="App">
        <Routes>
          <Route
            path="/user"
            element={
              <div>
                <Header />
                <Main />
              </div>
            }
          />
          <Route
            path="/"
            element={
              <div>
                <Header />
                <Main />
              </div>
            }
          />
          <Route path="/login" element={<Navigate to="/user" replace />} />
        </Routes>
      </div>
    );
  }
};

export default App;
