const initialState = {
  category: [],
  error: null,
};

const category = (state = initialState, action) => {
  switch (action.type) {
    case "category/get/peinding":
      return {
        ...state,
        loadCategory: true,
        error: null,
      };
    case "category/get/fulfilled":
      return {
        ...state,
        loadCategory: false,
        category: [...action.payload],
      };
    case "categoty/get/rejected":
      return {
        loadCategory: false,
        error: action.error,
      };
    case "category/add/peinding":
      return {
        ...state,
        loadCategory: true,
        error: null,
      };
    case "category/add/fulfilled":
      return {
        ...state,
        loadCategory: false,
        category: [...state.category, action.payload],
      };
    case "categoty/add/rejected":
      return {
        loadCategory: false,
        error: action.error,
      };
    default:
      return state;
  }
};

export const getCategory = () => {
  return async (dispatch) => {
    dispatch({ type: "category/get/peinding" });
    try {
      const res = await fetch("http://localhost:4000/category");
      const category = await res.json();
      dispatch({ type: "category/get/fulfilled", payload: category });
    } catch (error) {
      dispatch({ type: "category/get/rejected", error: error.toString() });
    }
  };
};
