import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Route, Routes, Link } from "react-router-dom";
import SigninForm from "./SigninForms/SigninForm";
import SignupForm from "./SigninForms/SignupForm";
import Newproduct from "./Newproduct";
import styles from "./Header.module.css";
// import { signOut } from "../../redux/features/application";

const Titile = () => {
  const userRole = useSelector((state) => state.application.role);
  const messageAdmin = useSelector((state) => state.application.message);
  // const token = useSel`ector((state) => state.application.token);

  const [modalLog, setModalLog] = useState(false);
  const [addProduct, setAddProduct] = useState(false);
  const handleOpenLoginForm = () => {
    modalLog ? setModalLog(false) : setModalLog(true);
  };
  // console.log(userSignin);
  const handleAddProduct = () => {
    addProduct ? setAddProduct(false) : setAddProduct(true);
  };

  // const handleOpenLogoutForm = () => {
  //   dispatch(signOut(login));
  // };
  return (
    <div className={styles.Header}>
      <div className={styles.titile}>
        <h1 className={styles.myShop}>
          <Link to="/" className={styles.link}>
            My Online Shop
          </Link>
        </h1>
        {/* {userRole === "admin" && (
          <div onClick={handleAddProduct} className={styles.addNewProduct}>
            добавить продукт
          </div>
        )}
        {addProduct && <Newproduct />} */}
        {userRole === "user" ? (
          <div className={styles.basket}>
            <img
              src="https://o.remove.bg/downloads/eea502df-396e-458e-b83f-3a0b1c7e2740/basket-removebg-preview.png"
              alt=""
            />
          </div>
        ) : userRole === "admin" ? (
          <div>
            <div>
              <h4>{messageAdmin}</h4>
            </div>
            <div>
              <Link
                to="/"
                // onClick={handleOpenLogoutForm}
                className={styles.buttonLog}
              >
                Выйти
              </Link>
            </div>
          </div>
        ) : (
          <div>
            <Link
              to="/login"
              onClick={handleOpenLoginForm}
              className={styles.buttonLog}
            >
              Войти
            </Link>
            {modalLog && (
              <div className={styles.modalFormLogin}>
                <div
                  className={styles.closeModalForm}
                  onClick={handleOpenLoginForm}
                >
                  x
                </div>
                <Routes>
                  <Route path="/login" element={<SigninForm />} />
                  <Route path="/logup" element={<SignupForm />} />
                </Routes>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Titile;
