const inittialState = {
  basket: [
    // { id, categoryId, amount },
    // { id, categoryId, amount },
  ],
  products: [],
  error: null,
};

const basket = (state = inittialState, action) => {
  switch (action.type) {
    case "get/basket/pending":
      return {
        ...state,
        geting: true,
        error: null,
      };
    case "get/basket/fulfilled":
      return {
        ...state,
        geting: false,
        basket: [action.payload],
      };
    case "get/basket/rejected":
      return {
        ...state,
        geting: false,
        error: action.error,
      };
    case "addproduct/inbasket/pending":
      return {
        ...state,
        adding: true,
        error: null,
      };
    case "addproduct/inbasket/fulfilled":
      const id = action.payload.productinBasket._id;
      const categoryId = action.payload.productinBasket.categoryId;
      const product = {
        ...action.payload.productLeft.find(
          (product) => product._id === action.payload.id
        ),
      };
      product.left--;
      return {
        ...state,
        adding: false,
        basket: [...state.basket, { id, categoryId, amount: 1 }],
        products: [...state.products, product],
      };
    case "addproduct/inbasket/rejected":
      return {
        ...state,
        adding: false,
        error: action.error,
      };
    case "deleteproduct/frombasket/pending":
      return {
        ...state,
        deleting: true,
        error: null,
      };
    case "deleteproduct/inbasket/fulfilled":
      return {
        ...state,
        deleting: false,
        basket: [
          ...state.basket.filter((product) => product._id !== action.payload),
        ],
      };
    case "deleteproduct/inbasket/rejected":
      return {
        ...state,
        deleting: false,
        error: action.error,
      };
    case "addamount/productinbasket/pending":
      return {
        ...state,
        addingAmout: true,
        error: null,
      };
    case "addamount/productinbasket/":
      return {
        ...state,
        addingAmout: false,
        basket: [
          ...state.basket.map((product) => {
            if (product.id === action.payload) {
              product.amount++;
            }
            return product;
          }),
        ],
        products: [
          ...state.products.map((product) => {
            if (product._id === action.payload) {
              product.left--;
            }
          }),
        ],
      };
    case "addamount/productinbasket/rejected":
      return {
        ...state,
        addingAmout: false,
        error: action.error,
      };
    case "removeamount/productinbasket/pending":
      return {
        ...state,
        removeAmout: true,
        error: null,
      };
    case "removeamount/productinbasket/":
      return {
        ...state,
        removeAmout: false,
        basket: [
          ...state.basket.map((product) => {
            if (product.id === action.payload) {
              product.amount--;
            }
            return product;
          }),
        ],
        products: [
          ...state.products.map((product) => {
            if (product._id === action.payload) {
              product.left++;
            }
          }),
        ],
      };
    case "removeamount/productinbasket/rejected":
      return {
        ...state,
        removeAmout: false,
        error: action.error,
      };
    default:
      return state;
  }
};

export const getAllProductsInBasket = () => {
  return async (dispatch) => {
    console.log(343);
    dispatch({ type: "get/basket/pending" });
    try {
      const res = await fetch("http://localhost:4000/basket");
      const basket = await res.json();
      dispatch({ type: "get/basket/fulfilled", payload: basket });
    } catch (error) {
      dispatch({ type: "get/basket/rejected", error: error });
    }
  };
};

export const addProductInBasket = (id) => {
  return async (dispatch, getState) => {
    dispatch({ type: "addproduct/inbasket/pending" });
    const state = getState();
    const productLeft = state.products.products;
    try {
      const res = await fetch(
        `http://localhost:4000/addproductinbasket/${id}`,
        {
          method: "PATCH",
          headers: {
            "Content-type": "application/json",
            Authorization: `Bearer ${state.application.token}`,
          },
        }
      );
      const productinBasket = await res.json();
      dispatch({
        type: "addproduct/inbasket/fulfilled",
        payload: {
          productinBasket,
          productLeft,
          id,
        },
      });
    } catch (error) {
      dispatch({ type: "addproduct/inbasket/rejected", error: error });
    }
  };
};

export const deleteProductFromBasket = (id) => {
  return async (dispatch, getState) => {
    dispatch({ type: "deleteproduct/frombasket/pending" });
    const state = getState();
    try {
      await fetch(`http://localhost:4000/deleteProductFromBasket/${id}`, {
