export const addAmountProductinBasket = (id) => {
  return async (dispatch, getState) => {
    const state = getState();
    dispatch({ type: "addamount/productinbasket/pending" });
    try {
      await fetch(`http://localhost:4000/addAmountProductinBasket/${id}`, {
