import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { addAmountProductinBasket, deleteProductFromBasket } from "../../../redux/features/basket";

const Productinbasket = ({ productInBasket, index }) => {
  const products = useSelector((state) => state.products.products);
  const product = products.find(
    (product) => product._id === productInBasket.id
  );
  const dispatch = useDispatch();
  const handleDeleteProductFromBasket = (id) => {
    dispatch(deleteProductFromBasket(id));
  };

  const handleChangeCountProduct = (id, incOrDec) => {
    if (incOrDec === "+") {
      dispatch(addAmountProductinBasket(id))
    }else {
      dispatch()
    }
  }
  return (
    <tr>
      <td>{index + 1}</td>
      <td>
        <img src={product.image} alt="Ахилесс" />
      </td>
      <td>{product.name}</td>
      <td>{productInBasket.left}</td>
      <td>
        <button onClick={() => handleChangeCountProduct(productInBasket.id, "+")}>+</button>
        <span>{productInBasket.amount}</span>
        <button onClick={() => handleChangeCountProduct(productInBasket.id, "-")}>-</button>
      </td>
      <td onClick={() => handleDeleteProductFromBasket(productInBasket.id)}>
        x
      </td>
    </tr>
  );
};

export default Productinbasket;
