import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { addProductInBasket } from "../../../redux/features/basket";
import { deleteProduct } from "../../../redux/features/products";
import styles from "./Products.module.css";

const Product = ({ product }) => {
  const dispatch = useDispatch();
  const role = useSelector((state) => state.application.role);
  const basket = useSelector((state) => state.basket.basket);
  const handleDeleteProduct = (id) => {
    dispatch(deleteProduct(id));
  };
  const handleAddProductToBasket = (id) => {
    dispatch(addProductInBasket(id));
  };

  const searchProduct = basket.find(
    (productBasket) => productBasket.id === product._id
  );
  return (
    <div className={styles.product}>
      {role === "admin" && (
        <div
          className={styles.deleteProduct}
          onClick={() => handleDeleteProduct(product._id)}
        >
          x
        </div>
      )}
      <div className={styles.img}>
        <img src={product.image} alt="" />
      </div>
      <div className={styles.info}>
        <div className={styles.name}>{product.name}</div>
        <div style={{ textAlign: "right", marginBottom: "15px" }}>
          <span className={styles.details}>details</span>
        </div>
        <div>
          <div>{product.price}</div>
          {product.oldPrice && <div>{product.oldPrice}</div>}
        </div>
      </div>

      <div className={styles.byeProduct}>
        <button
          className={searchProduct ? styles.buttonOne : styles.buttonTwo}
          disabled={searchProduct}
          onClick={() => handleAddProductToBasket(product._id)}
        >
          {searchProduct ? "в корзине" : "Добавить"}
        </button>
      </div>
    </div>
  );
};

export default Product;
